        if(maxcomplexity != null){
            formatOpts.add("maxcomplexity=" + maxcomplexity.toString());
        }

        if(maxlen != null){
            formatOpts.add("maxlen=" + maxlen.toString());
        }

        return formatOpts.toArray(new String[formatOpts.size()]);
    }
